mapper.select:
select ,CONCAT(#{imgUrl},product_img)as product_img from t_app_product where machine_id = #{machineId} and status !=4

and type = 0 ;

  
and type != 0 ;

 select * from t_app_product where status !=4
   
 and corp_id = #{corpId}
 
 
  and product_name like CONCAT('%',#{productName,jdbcType=VARCHAR},'%')
 
 
  and status = #{status} 
 
   
  and machine_id = #{machineId} 
 
  order by create_time desc
 
           limit
           #{startRow,jdbcType=INTEGER},#{endRow,jdbcType=INTEGER}

 select count(*) from t_app_product where status !=4
 
 and corp_id = #{corpId}
 
 
  and product_name like CONCAT('%',#{productName,jdbcType=VARCHAR},'%')
 
 
  and status = #{status}
