mapper.select:
select

from t_app_update_log
where 1 = 1

	and app_type = #{type,jdbcType = INTEGER}


	and app_version = #{version,jdbcType = INTEGER}

order by create_time desc limit #{startRow},#{endRow}

select count(id)
from t_app_update_log
where 1 = 1

	and app_type = #{type,jdbcType = INTEGER}


	and app_version = #{version,jdbcType = INTEGER}
